<?php

class Currency
{
	public $id;
	public $priority;
	public $isoCode;
	public $symbol;
	public $subunit;
	public $subunitToUnit;
	public $symbolFirst;
	public $htmlEntity;
	public $decimalMark;
	public $thousandsSeparator;

	public function __construct($id)
	{
		$this->id = strtolower($id);
		if (!isset(self::$_table[$this->id]))
			throw new Exception('unknown currency');

		foreach (self::$_table[$this->id] as $k => $v)
			$this->$k = $v;
	}

	public static function wrap($currency)
	{
		if (is_object($currency))
			return $currency;
		elseif (is_string($currency))
			return new self($currency);
	}

	public function symbolFirst()
	{
		return $this->symbolFirst;
	}

	public function decimalPlaces()
	{
		if ($this->subunitToUnit == 1)
			return 0;
		elseif ($this->subunitToUnit % 10 == 0)
			return floor(log10($this->subunitToUnit));
		else
			return floor(log10($this->subunitToUnit) + 1);
	}

	public function equals($c)
	{
		return $this->id === $c->id;
	}

	public function __toString()
	{
		return strtoupper($this->id);
	}

